export function generateRecipeCard(e){let i=document.querySelector(".recipes-container");i.replaceChildren(),e.map(e=>{let t=document.createElement("article");t.classList.add("recipe-card"),t.innerHTML=`
      <p class='recipe-time'>${e.time} min</p>
      <img class="recipe-image" src="./assets/petits-plats-photos/${e.image}" alt="${e.image}">
      <h4 class='plate-name'>${e.name}</h4>
      <section class='recipe-container'>
        <h5>Recette</h5>
        <p class='recipe-description'>${e.description}</p>
      </section>
      <section class='ingredients-container'>
        <h5>Ingr\xe9dients</h5>
        <ul class='ingredients'>
        ${e.ingredients.map(e=>{let i;return`<li class='ingredient'><p class='ingredient-name'>${e.ingredient} ${e.quantity?`<p class='ingredient-quantity'>${e.quantity} ${e.unit?`${e.unit}`:""}</p>`:""}</p> </li>`}).join("")}
        </ul>
      </section>`,i.appendChild(t)})}