export function searchRecipesByTitleIngredientsDescription(e,i){let s=[],t=e.length,n=[];for(let r=0;r<t;r++){let o=e[r],d=o.ingredients.length;for(let l=0;l<d;l++)if(o.ingredients[l].ingredient.toLowerCase().includes(i.toLowerCase())&&!n.includes(o.id)){n.push(o.id),s.push(o);break}(o.name.toLowerCase().includes(i.toLowerCase())||o.description.toLowerCase().includes(i.toLowerCase()))&&!n.includes(o.id)&&(n.push(o.id),s.push(o))}return s}