function searchRecipesByCategory(e,r,t){return e.filter(e=>e[t].toLowerCase().includes(r.toLowerCase()))}function searchRecipesByIngredient(e,r){return e.filter(e=>e.ingredients.some(e=>e.ingredient.toLowerCase()===r.toLowerCase()))}export function searchRecipesByTitleIngredientsDescription(e,r){let t=r.toLowerCase(),i=searchRecipesByCategory(e,t,"name"),n=searchRecipesByIngredient(e,t),s=searchRecipesByCategory(e,t,"description");return[...new Set([...i,...n,...s])]}